document.addEventListener('DOMContentLoaded', () => {
    const previewSize = 150;

    // Open modal
    document.querySelectorAll('[data-modal="true"]').forEach(button => {
        button.addEventListener('click', () => {
            const modalTarget = button.getAttribute('data-target');
            const modal = document.querySelector(modalTarget);
            if (modal) {
                modal.style.display = 'flex';
            }
        });
    });

    //Pre-filling out the form when editing client. Generated by chatGPT4o
    document.querySelectorAll('[data-client-id]').forEach(button => {
        button.addEventListener('click', async () => {
            const clientId = button.getAttribute('data-client-id');
            const modalId = button.getAttribute('data-target');
            const modal = document.querySelector(modalId);
            const form = modal.querySelector('form');

            try {
                const response = await fetch(`/Clients/GetClient?id=${clientId}`);
                if (!response.ok) throw new Error('Klienten kunde inte hämtas');

                const data = await response.json();

                // Fyll formulär
                form.querySelector('[name="Id"]').value = data.id;
                form.querySelector('[name="ClientName"]').value = data.clientName;
                form.querySelector('[name="Email"]').value = data.email;
                form.querySelector('[name="Location"]').value = data.location ?? '';
                form.querySelector('[name="Phone"]').value = data.phone ?? '';

                // Visa befintlig bild
                const imagePreview = form.querySelector('.image-preview');
                if (imagePreview && data.imagePath) {
                    imagePreview.src = `/${data.imagePath}`;
                    imagePreview.closest('.image-previewer')?.classList.add('selected');
                }

            } catch (error) {
                console.error('Fel vid hämtning av klientdata:', error);
            }
        });
    });

    // Pre-filling out the form when editing member. Generated by chatGPT4o
    document.querySelectorAll('[data-member-id]').forEach(button => {
        button.addEventListener('click', async () => {
            const memberId = button.getAttribute('data-member-id');
            const modalId = button.getAttribute('data-target');
            const modal = document.querySelector(modalId);
            const form = modal.querySelector('form');

            try {
                const response = await fetch(`/Members/GetMember?id=${memberId}`);
                if (!response.ok) throw new Error('Medlemmen kunde inte hämtas');

                const data = await response.json();
                console.log("Member-data till editmodal:", data);

                // Fyll formulärfält
                form.querySelector('[name="Id"]').value = data.id;
                form.querySelector('[name="FirstName"]').value = data.firstName;
                form.querySelector('[name="LastName"]').value = data.lastName;
                form.querySelector('[name="Email"]').value = data.email;
                form.querySelector('[name="Phone"]').value = data.phone ?? '';
                form.querySelector('[name="JobTitle"]').value = data.jobTitle ?? '';
                form.querySelector('[name="StreetName"]').value = data.streetName ?? '';
                form.querySelector('[name="PostalCode"]').value = data.postalCode ?? '';
                form.querySelector('[name="City"]').value = data.city ?? '';
                form.querySelector('[name="DateOfBirth"]').value = data.dateOfBirth?.split("T")[0] ?? '';
                form.querySelector('[name="SelectedRole"]').value = data.selectedRole ?? '';

                // Visa befintlig bild
                const imagePreview = form.querySelector('.image-preview');
                if (imagePreview && data.imagePath) {
                    imagePreview.src = `/${data.imagePath}`;
                    imagePreview.closest('.image-previewer')?.classList.add('selected');
                }

            } catch (error) {
                console.error('Fel vid hämtning av medlemsdata:', error);
            }
        });
    });






    // Close modal
    document.querySelectorAll('[data-close="true"]').forEach(button => {
        button.addEventListener('click', () => {
            const modal = button.closest('.modal');
            if (modal) {
                modal.style.display = 'none';

                // Clear form
                modal.querySelectorAll('form').forEach(form => {
                    form.reset();

                    // Clear image preview
                    const imagePreview = form.querySelector('.image-preview');
                    if (imagePreview) {
                        imagePreview.src = '';
                    }
                    const imagePreviewer = form.querySelector('.image-previewer');
                    if (imagePreviewer) {
                        imagePreviewer.classList.remove('selected');
                    }
                });
            }
        });
    });

    // Handle image previewer
    document.querySelectorAll('.image-previewer').forEach(previewer => {
        const fileInput = previewer.querySelector('input[type="file"]');
        const imagePreview = previewer.querySelector('.image-preview');

        previewer.addEventListener('click', () => fileInput.click());

        fileInput.addEventListener('change', ({ target: { files } }) => {
            const file = files[0];
            if (file) {
                processImage(file, imagePreview, previewer, previewSize);
            }
        });
    });



    // Handle form submission
    const modalForms = document.querySelectorAll('form[data-modal-form="true"]');
    modalForms.forEach(form => {
        form.addEventListener('submit', async (e) => {
            e.preventDefault();
            console.log("Member form submit triggered!"); //Ta bort sen



            clearErrorMessages(form);

            const formData = new FormData(form);

            try {
                const res = await fetch(form.action, {
                    method: 'POST',
                    body: formData
                });


                if (res.ok) {
                    const modal = form.closest('.modal');
                    if (modal) {
                        modal.style.display = 'none';
                    }
                        

                        window.location.reload();
                }
                else if (res.status === 400) {
                    const data = await res.json();

                    if (data.errors) {
                        Object.keys(data.errors).forEach(key => {
                            let input = form.querySelector(`[name="${key}"]`);
                            if (input) {
                                input.classList.add('input-validation-error');
                            }

                            const span = form.querySelector(`[data-valmsg-for="${key}"]`);
                            if (span) {
                                span.innerText = data.errors[key].join('\n');
                                span.classList.add('field-validation-error');
                            }
                        });
                    }
                }
            }
            catch (e) {
                console.error('Error submitting the form', e);
            }
        });
    });
});



// Funktion för att rensa felmeddelanden
function clearErrorMessages(form) {
    form.querySelectorAll('[data-val="true"]').forEach(input => {
        input.classList.remove('input-validation-error');
    });

    form.querySelectorAll('[data-valmsg-for]').forEach(span => {
        span.innerText = '';
        span.classList.remove('field-validation-error');
    });
}


// Funktion för att ladda in bild
async function loadImage(file) {
    return new Promise((resolve, reject) => {
        const reader = new FileReader();

        reader.onerror = () => reject(new Error('Error reading file'));
        reader.onload = (e) => {
            const img = new Image();
            img.onerror = () => reject(new Error('Error loading image'));
            img.onload = () => resolve(img);
            img.src = e.target.result;
        };

        reader.readAsDataURL(file);
    });
}

// Funktion för att hantera bildförhandsvisning
async function processImage(file, imagePreview, previewer, previewSize = 150) {
    try {
        const img = await loadImage(file);
        const canvas = document.createElement('canvas');
        canvas.width = previewSize;
        canvas.height = previewSize;

        const ctx = canvas.getContext('2d');
        ctx.drawImage(img, 0, 0, previewSize, previewSize);
        imagePreview.src = canvas.toDataURL('image/jpeg');
        previewer.classList.add('selected');
    } catch (error) {
        console.error('Failed on image-processing', error);
    }
}
